insert into project (project_id, project_name, bbox, project_type, project_goal) values ('0','Mainz', '{"xmin": 8.24287, "ymin": 49.992401, "xmax": 8.29617, "ymax": 50.018199}', 'transportation_planning', 'Die Mainzer Mobilität plant eine neue Straßenbahnstrecke vom <span class="font-weight-bold">Schillerplatz</span> über das <span class="font-weight-bold">Höfchen</span> in Richtung <span class="font-weight-bold">Mainzer Neustadt</span>. Mit ihr soll die Altstadt künftig direkter mit dem bevölkerungsreichsten Mainzer <span class="text-decoration-underline">Neustadt-Viertel</span> verbunden werden.</br> Es wurden drei Varianten einer Streckenführung erstellt, die Sie im folgenden kommentieren können.');